import React, { useState, useEffect } from 'react';
import { Button } from './Button';
import { Link } from 'react-router-dom';
import './Navbar.css';


function Navbar() {
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const showButton = () => {
    if (window.innerWidth <= 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };

  useEffect(() => {
    showButton();
  }, []);

  window.addEventListener('resize', showButton);

  return (
    <>
      <nav className='navbar'>
        <div className='navbar-container'>
          <div to='/' className='navbar-logo' onClick={closeMobileMenu} >
            <i class='fab fa-typo3' />
          </div>
          <div className='menu-icon' onClick={handleClick}>
            <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
          </div>
          <ul className={click ? 'nav-menu active' : 'nav-menu'}>
            <li className='nav-item'>
              <Link to='/' className='nav-links' onClick={closeMobileMenu}>
                Home
              </Link>
            </li>
            <li className='nav-item'>
              <Link
                to='/services'
                className='nav-links'
                onClick={closeMobileMenu}
              >
                Menu
              </Link>
            </li>
            <li className='nav-item'>
              <Link
                to='/products'
                className='nav-links'
                onClick={closeMobileMenu}
              >
                Gallery
              </Link>
            </li>
            <li className='nav-item'>
              <Link
                to='/products'
                className='nav-links'
                onClick={closeMobileMenu}
              >
                Specials
              </Link>
            </li>
            <li className='nav-item'>
              <Link
                to='/products'
                className='nav-links'
                onClick={closeMobileMenu}
              >
                Contact
              </Link>
            </li>

            <li>
              <a
                href="tel:1-801-282-0670"
                className='nav-links-mobile'
                onClick={closeMobileMenu}
              >
                (801) 282-0670
              </a>
            </li>

            <li className='nav-item'>
            <a
              class='facebook nav-links-mobile'
              href='https://www.facebook.com/'
              target='_blank'
              aria-label='Facebook'
              onClick={closeMobileMenu}
            >
              Facebook<i class='fab fa-facebook-f' />
            </a>
            </li>

            <li className='nav-item'>
            <a
              class='instagram nav-links-mobile'
              href='https://www.yelp.com/'
              target='_blank'
              aria-label='Instagram'
              onClick={closeMobileMenu}
            >
              Yelp<i class='fab fa-yelp' />
            </a>
            </li>   

          </ul>
          {button && <Button 
            // class='social-icons' 
            buttonStyle='btn--outline'
            >(801) 282-0670
          </Button>}
          {button && <a
            class='social-icon-link facebook'
            href='https://www.facebook.com/'
            target='_blank'
            aria-label='Facebook'
            onClick={closeMobileMenu}
          >
            <i class='fab fa-facebook-f' />
          </a>}
           {button && <a
            class='social-icon-link yelp'
            href='https://www.yelp.com/'
            target='_blank'
            aria-label='Facebook'
            onClick={closeMobileMenu}
          >
            <i class='fab fa-yelp' />
          </a>}
        </div>
      </nav>
    </>
  );
}

export default Navbar;
