import React, { useEffect, useState } from "react";
import "./Menu.css";
import Footer from "../Footer";
import images from "./menu_items/appetizers.js";
import salads from "./menu_items/specialty_salads";
import Popup from "reactjs-popup";
import "reactjs-popup/dist/index.css";

export default function Gallery() {
  const [tag, setTag] = useState("all");
  const [filteredImages, setFilteredImages] = useState([]);

  useEffect(() => {
    tag === "all"
      ? setFilteredImages(images,salads)
      : setFilteredImages(images.filter((image) => image.tag === tag));
  }, [tag]);
  return (
    <>
      <h1 className="menu">Menu</h1>
      <div className="menu_App">
        <div className="tags">
          <TagButton
            name="all"
            tagActive={tag === "all" ? true : false}
            handleSetTag={setTag}
          />{" "}
          /
          <TagButton
            name="appetizers"
            tagActive={tag === "appetizers" ? true : false}
            handleSetTag={setTag}
          />{" "}
          /
          <TagButton
            name="kitchen food"
            tagActive={tag === "kitchen food" ? true : false}
            handleSetTag={setTag}
          />{" "}
          /
          <TagButton
            name="other"
            tagActive={tag === "other" ? true : false}
            handleSetTag={setTag}
          />
        </div>
        {/* <SRLWrapper options={options}> */}
        <h1 className="menu" id="tag_header">{tag}</h1>
        <div className="menu_container">
          {filteredImages.map((image) => (
            <div key={image.id} className="image-card">
              {image.id === "title" ? <h1> Testing </h1> : ""}
              {/* <a href={`/imagesGallery/${image.imageName}`}>
                <p>Test</p>
                <img
                  className="image"
                  src={`/imagesGallery/${image.imageName}`}
                  alt=""
                />
              </a> */}


            </div>
          ))}
        </div>
        {/* </SRLWrapper> */}
      </div>
      {/* </SimpleReactLightBox>
      </React.StrictMode> */}
      <a href="#tag_header"> Go to header </a>
      <Footer />
    </>
  );
}

const TagButton = ({ name, handleSetTag, tagActive }) => {
  return (
    <button
      className={`tag ${tagActive ? "active" : null}`}
      onClick={() => handleSetTag(name)}
    >
      {name.toUpperCase()}
    </button>
  );
};
