import React, { useEffect, useState } from "react";
import "./Menu.css";
import Footer from "../Footer";
import images from "./menu_items/appetizers.js";
import salads from "./menu_items/specialty_salads";
import Menu_item from "./menu_items/Menu_item";

export default function Menu() {
  const [tag, setTag] = useState("all");
  const [filteredImages, setFilteredImages] = useState([]);

  useEffect(() => {
    tag === "all"
      ? setFilteredImages(images, salads)
      : setFilteredImages(images.filter((image) => image.tag === tag));
  }, [tag]);
  return (
    <>
      <h1 className="menu_page">Menu</h1>
      <div className="menu_App">
        <div className="menu_tags">
          <TagButton
            name="all"
            tagActive={tag === "all" ? true : false}
            handleSetTag={setTag}
          />{" "}
          /
          <TagButton
            name="appetizers"
            tagActive={tag === "appetizers" ? true : false}
            handleSetTag={setTag}
          />{" "}
          /
          <TagButton
            name="kitchen food"
            tagActive={tag === "kitchen food" ? true : false}
            handleSetTag={setTag}
          />{" "}
          /
          <TagButton
            name="other"
            tagActive={tag === "other" ? true : false}
            handleSetTag={setTag}
          />
        </div>
        <h1 className="menu_title" id="tag_header">
          {tag}
        </h1>
        <div className="menu_container">
          <Menu_item filteredImages={filteredImages} />
        </div>
        <h1 className="menu_title" id="tag_header">
          Specialty Salads
        </h1>
        <div className="menu_container" id="specialty_salads">
          <Menu_item filteredImages={salads} />
        </div>
      </div>
      <Footer />
    </>
  );
}

const TagButton = ({ name, handleSetTag, tagActive }) => {
  return (
    <button
      className={`menu_tag ${tagActive ? "active" : null}`}
      onClick={() => handleSetTag(name)}
    >
      {name.toUpperCase()}
    </button>
  );
};
