import React, { useEffect, useState } from 'react';
import '../../App.css';
import images from './data';
import { SRLWrapper } from 'simple-react-lightbox';

const options = {
	// settings: {
	// 	overlayColor: 'rgb(25, 136, 124)',
	// 	autoplaySpeed: 1500,
	// 	transitionSpeed: 900
	// },
	buttons: {
		// backgroundColor: 'red',
		// iconColor: 'rgba(126, 172, 139, 0.8)'
		showDownloadButton: false,
	},
	// caption: {
	// 	captionColor: '#a6cfa5',
	// 	captionFontFamily: 'Raleway, sans-serif',
	// 	captionFontWeight: '300',
	// 	captionTextTransform: 'uppercase'
	// },
	// progressBar: {
	// 	height: '20px',
	// 	fillColor: 'blue',
	// 	backgroundColor: 'white'
	// }
	// settings: {
	// 	autoplaySpeed: 3000,
	// 	boxShadow: 'none',
	// 	disableKeyboardControls: false,
	// 	disablePanzoom: false,
	// 	disableWheelControls: false,
	// 	hideControlsAfter: 3000,
	// 	lightboxTransitionSpeed: 0.3,
	// 	lightboxTransitionTimingFunction: 'linear',
	// 	overlayColor: 'rgba(30, 30, 30, 0.9)',
	// 	slideAnimationType: 'fade',
	// 	slideSpringValues: [300, 50],
	// 	slideTransitionSpeed: 0.6,
	// 	slideTransitionTimingFunction: 'linear',
	// 	usingPreact: false
	//   }
};

function Gallery_app() {
	const [tag, setTag] = useState('all');
	const [filteredImages, setFilteredImages] = useState([]);

	useEffect(
		() => {
			tag === 'all' ? setFilteredImages(images) : setFilteredImages(images.filter(image => image.tag === tag));
		},
		[tag]
	);

	return (
		<div className="gallery">
			<div className="tags">
				<TagButton name="all" tagActive={tag === 'all' ? true : false} handleSetTag={setTag} /> /
				<TagButton name="new" tagActive={tag === 'new' ? true : false} handleSetTag={setTag} /> /
				<TagButton name="free" tagActive={tag === 'free' ? true : false} handleSetTag={setTag} /> /
				<TagButton name="pro" tagActive={tag === 'pro' ? true : false} handleSetTag={setTag} />
			</div>
			<SRLWrapper options={options}>
				<div className="container">
					{filteredImages.map(image => (
						<div key={image.id} className="image-card">
							<a href={`/images/${image.imageName}`}>
								<img className="image" src={`/images/${image.imageName}`} alt="" />
							</a>
						</div>
					))}
				</div>
			</SRLWrapper>
		</div>
	);
}

const TagButton = ({ name, handleSetTag, tagActive }) => {
	return (
		<button className={`tag ${tagActive ? 'active' : null}`} onClick={() => handleSetTag(name)}>
			{name.toUpperCase()}
		</button>
	);
};

export default Gallery_app;
