import React, { useEffect, useState } from "react";
import "./Menu.css";
import Footer from "../Footer";
import images from "./Gallery_data";
import Popup from "reactjs-popup";
import "reactjs-popup/dist/index.css";

export default function Gallery() {
  const [tag, setTag] = useState("all");
  const [filteredImages, setFilteredImages] = useState([]);

  useEffect(() => {
    tag === "all"
      ? setFilteredImages(images)
      : setFilteredImages(images.filter((image) => image.tag === tag));
  }, [tag]);
  return (
    <>
      <h1 className="menu">Menu</h1>
      <div className="menu_App">
        <div className="tags">
          <TagButton
            name="all"
            tagActive={tag === "all" ? true : false}
            handleSetTag={setTag}
          />{" "}
          /
          <TagButton
            name="sushi"
            tagActive={tag === "sushi" ? true : false}
            handleSetTag={setTag}
          />{" "}
          /
          <TagButton
            name="kitchen food"
            tagActive={tag === "kitchen food" ? true : false}
            handleSetTag={setTag}
          />{" "}
          /
          <TagButton
            name="other"
            tagActive={tag === "other" ? true : false}
            handleSetTag={setTag}
          />
        </div>
        {/* <SRLWrapper options={options}> */}
        <div className="container">
          {filteredImages.map((image) => (
            <div key={image.id} className="image-card">
              <a href={`/imagesGallery/${image.imageName}`}>
                <p>Test</p>
                <img
                  className="image"
                  src={`/imagesGallery/${image.imageName}`}
                  alt=""
                />
              </a>
            </div>
          ))}
        </div>
        {/* </SRLWrapper> */}
      </div>
      {/* </SimpleReactLightBox>
      </React.StrictMode> */}
     <Popup
    trigger={<button className="button"> Open Modal </button>}
    modal
    nested
  >
    {close => (
      <div className="modal">
        <button className="close" onClick={close}>
          &times;
        </button>
        <div className="header"> Modal Title </div>
        <div className="content">
          {' '}
          Lorem ipsum dolor sit amet consectetur adipisicing elit. Atque, a nostrum.
          Dolorem, repellat quidem ut, minima sint vel eveniet quibusdam voluptates
          delectus doloremque, explicabo tempore dicta adipisci fugit amet dignissimos?
          <br />
          Lorem ipsum dolor sit amet, consectetur adipisicing elit. Consequatur sit
          commodi beatae optio voluptatum sed eius cumque, delectus saepe repudiandae
          explicabo nemo nam libero ad, doloribus, voluptas rem alias. Vitae?
        </div>
        <div className="actions">
          <Popup
            trigger={<button className="button"> Trigger </button>}
            position="top center"
            nested
          >
            <span>
              Lorem ipsum dolor sit amet, consectetur adipisicing elit. Beatae
              magni omnis delectus nemo, maxime molestiae dolorem numquam
              mollitia, voluptate ea, accusamus excepturi deleniti ratione
              sapiente! Laudantium, aperiam doloribus. Odit, aut.
            </span>
          </Popup>
          <button
            className="button"
            onClick={() => {
              console.log('modal closed ');
              close();
            }}
          >
            close modal
          </button>
        </div>
      </div>
    )}
  </Popup>
      <Footer />
    </>
  );
}

const TagButton = ({ name, handleSetTag, tagActive }) => {
  return (
    <button
      className={`tag ${tagActive ? "active" : null}`}
      onClick={() => handleSetTag(name)}
    >
      {name.toUpperCase()}
    </button>
  );
};
