import React, { useEffect, useState } from "react";
import "./Menu.css";
import Footer from "../Footer";
import images from "./Menu_data";
import { SRLWrapper } from "simple-react-lightbox";
import SimpleReactLightBox from "simple-react-lightbox";

const options = {
  // settings: {
  // 	overlayColor: 'rgb(25, 136, 124)',
  // 	autoplaySpeed: 1500,
  // 	transitionSpeed: 900
  // },
  buttons: {
    // backgroundColor: 'red',
    // iconColor: 'rgba(126, 172, 139, 0.8)',
    showDownloadButton: false,
  },
};

export default function Menu() {
  const [tag, setTag] = useState("all");
  const [filteredImages, setFilteredImages] = useState([]);

  useEffect(() => {
    tag === "all"
      ? setFilteredImages(images)
      : setFilteredImages(images.filter((image) => image.tag === tag));
  }, [tag]);
  return (
    <>
      <h1 className="menu">Menu</h1>
      {/* <React.StrictMode> */}
      {/* <SimpleReactLightBox> */}
      <div className="App">
        <div className="tags">
          <TagButton
            name="all"
            tagActive={tag === "all" ? true : false}
            handleSetTag={setTag}
          />{" "}
          /
          <TagButton
            name="appetizer"
            tagActive={tag === "sushi" ? true : false}
            handleSetTag={setTag}
          />{" "}
          /
          <TagButton
            name="kitchen food"
            tagActive={tag === "kitchen food" ? true : false}
            handleSetTag={setTag}
          />{" "}
          /
          <TagButton
            name="other"
            tagActive={tag === "other" ? true : false}
            handleSetTag={setTag}
          />
        </div>
        {/* <SRLWrapper options={options}> */}
        <div className="container">
          {filteredImages.map((image) => (
            <div key={image.id} className="image-card box">
              {/* <a href={`/imagesGallery/${image.imageName}`}> */}
              <a class="button" href="#popup1">
                <p>{image.name}</p>
                <p>{image.description}</p>
                <img
                  className="image"
                  src={`/imagesGallery/${image.imageName}`}
                  alt=""
                />
              </a>
            </div>
          ))}
        </div>
        {/* </SRLWrapper> */}
      </div>
      {/* </SimpleReactLightBox> */}
      {/* </React.StrictMode> */}

      <div id="popup1" class="overlay">
        <div class="popup">
          <h2>Here i am</h2>
          <a class="close" href="#">
            &times;
          </a>
          <div class="content">
            Thank to pop me out of that button, but now i'm done so you can
            close this window.
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
}
