import React, { useEffect, useState } from "react";
// import "./Menu.css";
import Footer from "../Footer";
import images from "./menu_items/appetizers.js";
import salads from "./menu_items/specialty_salads";
import Popup from "reactjs-popup";
import "reactjs-popup/dist/index.css";

export default function Menu() {
  const [tag, setTag] = useState("all");
  const [filteredImages, setFilteredImages] = useState([]);

  useEffect(() => {
    tag === "all"
      ? setFilteredImages(images, salads)
      : setFilteredImages(images.filter((image) => image.tag === tag));
  }, [tag]);
  return (
    <>
      <h1 className="menu">Menu</h1>
      <div className="menu_App">
        <div className="tags">
          <TagButton
            name="all"
            tagActive={tag === "all" ? true : false}
            handleSetTag={setTag}
          />{" "}
          /
          <TagButton
            name="appetizers"
            tagActive={tag === "appetizers" ? true : false}
            handleSetTag={setTag}
          />{" "}
          /
          <TagButton
            name="kitchen food"
            tagActive={tag === "kitchen food" ? true : false}
            handleSetTag={setTag}
          />{" "}
          /
          <TagButton
            name="other"
            tagActive={tag === "other" ? true : false}
            handleSetTag={setTag}
          />
        </div>
        {/* <SRLWrapper options={options}> */}
        <h1 className="menu" id="tag_header">
          {tag}
        </h1>
        {/* {tag === "all" ? } */}
        <div className="menu_container">
          {filteredImages.map((image) => (
            <div key={image.id} className="image-card">
              {/* {image.id === "title" ? <h1> Testing </h1> : ""} */}
              {/* <a href={`/imagesGallery/${image.imageName}`}>
                <p>Test</p>
                <img
                  className="image"
                  src={`/imagesGallery/${image.imageName}`}
                  alt=""
                />
              </a> */}

              <Popup
                trigger={
                  <button className="button">
                    <div className="ixMaBu">
                      <div className="esGvJU">
                        <div className="klbrrx">
                          <img
                            className="image iUxyLI"
                            src={`/imagesGallery/${image.imageName}`}
                            alt=""
                          />
                        </div>
                      </div>

                      <div className="LjBBC">
                        <div className="iUYAiH">
                          <span className="izBrpx">{image.name}</span>
                        </div>
                        <span className="kmvjNe">{image.description}</span>
                        <div className="djHviU">
                          <span className="iSLJhq">{image.price}</span>
                        </div>
                      </div>
                    </div>
                  </button>
                }
                modal
                nested
              >
                {(close) => (
                  <div className="modal">
                    <button className="close" onClick={close}>
                      &times;
                    </button>
                    <div className="header">
                      {" "}
                      {image.name} {image.price}{" "}
                    </div>
                    <div className="content"> {image.description}</div>
                    <img
                      className="image"
                      src={`/imagesGallery/${image.imageName}`}
                      alt=""
                    />
                    {/* <div className="actions">
                      <Popup
                        trigger={<button className="button"> Trigger </button>}
                        position="top center"
                        nested
                      >
                        <span>
                          Lorem ipsum dolor sit amet, consectetur adipisicing
                          elit. Beatae magni omnis delectus nemo, maxime
                          molestiae dolorem numquam mollitia, voluptate ea,
                          accusamus excepturi deleniti ratione sapiente!
                          Laudantium, aperiam doloribus. Odit, aut.
                        </span>
                      </Popup>
                      <button
                        className="button"
                        onClick={() => {
                          console.log("modal closed ");
                          close();
                        }}
                      >
                        close modal
                      </button>
                    </div> */}
                  </div>
                )}
              </Popup>
            </div>
          ))}
        </div>
        {/* </SRLWrapper> */}
      </div>
      {/* </SimpleReactLightBox>
      </React.StrictMode> */}
      <a href="#tag_header"> Go to header </a>
      <Footer />
    </>
  );
}

const TagButton = ({ name, handleSetTag, tagActive }) => {
  return (
    <button
      className={`tag ${tagActive ? "active" : null}`}
      onClick={() => handleSetTag(name)}
    >
      {name.toUpperCase()}
    </button>
  );
};
